mot = input("choisissez un mot: ")boucle = Falsemot_recherche = ["_"]*len(mot)vie = 10while boucle == False:    mot_final= str()    k = False    print("mot a trouver: " ,mot_recherche)    lettre = input("lettre ou mot: ")    j = -1    for i in mot:        j += 1        if lettre == i:            print("bonne lettre")            mot_recherche[j] = lettre            k = True    for i in mot_recherche:        mot_final += i    if mot_final == mot or lettre == mot:        print("gagné !le mot est : ", mot)        boucle = True        k = True    if k == False:        vie = vie - 1         if vie != 0:            print("dommage, il vous reste ",vie,"vie(s)")        else:            print("dommage, vous êtes mort(e)...")            boucle = True